import React from 'react'
import { useState } from 'react';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import FloatingLabel from 'react-bootstrap/FloatingLabel';
import Form from 'react-bootstrap/Form';
handleAddCategory



function Category() {





  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow = () => setShow(true);

  const [categoryItem, setcategoryItem] = useState({


    id: "",
    name: "",
    allVideos: []


  })


  const addcategoryForm = (e) => {

   const {name,value}=e.target

   setcategoryItem({...categoryItem,[name]:value})

  }

  console.log(categoryItem);

  return (

    <>


      <div className="d-grid">
        <div onClick={handleShow} className='btn btn-dark m-2'>

          Add category

        </div>

      </div>


      <Modal
        show={show}
        onHide={handleClose}
        backdrop="static"
        keyboard={false}
      >
        <Modal.Header closeButton>
          <Modal.Title>Add category</Modal.Title>
        </Modal.Header>
        <Modal.Body>


          <FloatingLabel className='mb-3' controlId="floatingId" label="Id">
            <Form.Control type="text" name='id' onChange={addcategoryForm} placeholder="Id" />
          </FloatingLabel>




          <FloatingLabel className='mb-3' controlId="floatingId" label="category">
            <Form.Control type="text" name='name' onChange={addcategoryForm} placeholder="category" />
          </FloatingLabel>


        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button onClick={handleAddCategory} variant="primary">Add</Button>
        </Modal.Footer>
      </Modal>

    </>

  )
}

export default Category
