import React from 'react'
import Card from 'react-bootstrap/Card';
import { Trash2 } from 'react-feather';
import { useState } from 'react';
// import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import { deleteVideo } from '../services/allapi';
import { v4 as uuidv4 } from 'uuid';
add


function Videocard({card,handleDeleteStatus}) {

  const [show, setShow] = useState(false);

  const handleClose = () => setShow(false);
  const handleShow =async() => {
    
    
    
    setShow(true);

    // to generate id automatically

    const uid=uuidv4();

    console.log(uid);

    // to generate system date and time

    let cardTime=new Date()

    console.log(cardTime);


    const{caption,url}=card

    if(uid!="",caption!="",url!="",cardTime!=""){


        const body={

          id:uid,cardName:caption,url,date:cardTime

        }
      
        Addhistory(body)

    }
    
  
  
  
  
  
  }

    // function definition

    const removeItem=async(id)=>{

      // api call to delete specific card
      
       let response=await deleteVideo (id)
       console.log(response);

       if(response.status>=200&&response.status<300){

        handleDeleteStatus(true)

       }
      
    }


  return (

    <>

      <div>

        <Card  className='shadow'>
          <Card.Img onClick={handleShow} variant="top" height={'200px'} src={card?.thumbnail} />
          <Card.Body>
            <Card.Title>
              <span>{card?.caption}</span>
              < Trash2 onClick={()=>removeItem(card?.id)} color='red' style={{ float: 'right' }} />
            </Card.Title>

          </Card.Body>
        </Card>


        <Modal show={show} onHide={handleClose}>
          <Modal.Header closeButton>
            <Modal.Title>Video Caption</Modal.Title>

          </Modal.Header>
          <Modal.Body>

          <iframe width={'100%'} height={'400px'} src={`${card?.url}?autoplay=1 `} title="Jawan: Hayyoda (Tamil) | Shahrukh Khan | Atlee | Anirudh | Nayanthara | Vijay S | Priya Mali | Vivek" frameborder="0" allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture; web-share" allowfullscreen></iframe>

          </Modal.Body>
         
        </Modal>

      </div>

    </>

  )
}

export default Videocard
